var date_today = new Date().toISOString().slice(0, 10);

//items[0].json.date_today = date;

return {date_today};

//Master Board Column, Get Master Board Item
var masterBoardColumns = $node["Get Board ALL JOBS Item"].json;
var targetBoardColumns = $node["Get Item Board #2"].json;

//If you want to ignore some columns you can add them over here. If not required, just use []
var ignoreColumnFromMaster = [];
var outuputJSON = {};

// For all columns in the Master Board
// 
masterBoardColumns.column_values.forEach(function(data) {
  // Get the targetColumnId
  // Filter the target board columns to those who have the same title and data type, THEN return the target column ID
  // Returns an array of target column ID's
  var targetColumnId = targetBoardColumns.column_values.filter(obj => obj.title == data.title && obj.type == data.type).map(filteredObj => filteredObj.id);

  // If Target Column Array exists, has an element and we are not ignoring this column
  // THEN form the JSON depending on the data type
  if (typeof targetColumnId !== 'undefined' && targetColumnId && targetColumnId.length > 0 && !ignoreColumnFromMaster.includes(data.title)) {
    switch (data.type) {
      case "text":
        outuputJSON[targetColumnId] = data.text ? data.text : "";
        break;

      case "numeric":
        outuputJSON[targetColumnId] = data.value ? (data.value.replace(/[^0-9]/g, '')) :"";
        break;

      case "date":
        outuputJSON[targetColumnId] = data.text ? {
          "date": data.text
          // "time": "00:00:00"
        } : {};
        break;

      case "board-relation":
        var bData = data.value;
        if (bData !== undefined && bData !== null && bData.constructor == String) {
          bData = JSON.parse(bData);
        }
        if (bData != null) {
          var dependencyList = bData.linkedPulseIds.map(u => u.linkedPulseId);
          outuputJSON[targetColumnId] = {
            item_ids: dependencyList
          };
        }

        break;
        // Status Tye
      case "color":
        if (data.text) {
          outuputJSON[targetColumnId] = {
            label: data.text
          };
        }

        break;
        // Timeline Type
      case "timerange":
        if (data.value) {
          var bData = data.value;
          if (bData !== undefined && bData !== null && bData.constructor == String) {
            bData = JSON.parse(bData);
          }
          if (bData != null) {
            outuputJSON[targetColumnId] = {
              to: bData.to,
              from: bData.from
            };
          }
        }
        break;
      default:
    }
  }
});

let result = JSON.stringify(outuputJSON);

return {
  result
};
